import React, { useState } from "react";
//import VehicleFilterSidebar from "./VehicleFilterSidebar";
import "../Css/Catlog.css";
import NavBar from "./NavBar";

const vehiclesData = [
  {
    id: 1,
    make: "Toyota",
    model: "Camry",
    price: 25000,
    year: 2022,
    type: "Sedan",
    image: "data:image/jpeg;base64,",
  },
  {
    id: 2,
    make: "Ford",
    model: "Mustang",
    price: 55000,
    year: 2023,
    type: "Sports",
    image: "data:image/jpeg;base64,",
  },
  {
    id: 3,
    make: "Tesla",
    model: "Model S",
    price: 90000,
    year: 2023,
    type: "Electric",
    image: "data:image/jpeg;base64,",
  },
];

const VehicleCatalogPage = () => {
  const [vehicles, setVehicles] = useState(vehiclesData);
  const [sortBy, setSortBy] = useState("");

  const handleFilterChange = (filters: { make: string; model: string; priceRange: any; year: string; type: string; }) => {
    let filteredVehicles = vehiclesData.filter((vehicle) => {
      return (
        (filters.make
          ? vehicle.make.toLowerCase().includes(filters.make.toLowerCase())
          : true) &&
        (filters.model
          ? vehicle.model.toLowerCase().includes(filters.model.toLowerCase())
          : true) &&
        (filters.priceRange
          ? checkPriceRange(vehicle.price, filters.priceRange)
          : true) &&
        (filters.year ? vehicle.year.toString() === filters.year : true) &&
        (filters.type ? vehicle.type === filters.type : true)
      );
    });
    setVehicles(filteredVehicles);
  };

  const checkPriceRange = (price: number, range: { split: (arg0: string) => { (): any; new(): any; map: { (arg0: NumberConstructor): [any, any]; new(): any; }; }; }) => {
    const [min, max] = range.split("-").map(Number);
    return price >= min && price <= max;
  };

  const handleSortChange = (e: { target: { value: any; }; }) => {
    const value = e.target.value;
    setSortBy(value);
    let sortedVehicles = [...vehicles];
    if (value === "price") sortedVehicles.sort((a, b) => a.price - b.price);
    if (value === "year") sortedVehicles.sort((a, b) => b.year - a.year);
    setVehicles(sortedVehicles);
  };

  return (
    <><NavBar /><div className="vehicle-catalog">
      <div className="catalog-content">
        <div className="sorting-options">
          <label>Sort By: </label>
          <select onChange={handleSortChange}>
            <option value="">None</option>
            <option value="price">Price</option>
            <option value="year">Year</option>
          </select>
        </div>
        <div className="vehicle-list">
          {vehicles.map((vehicle) => (
            <div key={vehicle.id} className="vehicle-card">
              <img
                src={vehicle.image}
                alt={vehicle.model}
                className="vehicle-image" />
              <h3>
                {vehicle.make} {vehicle.model}
              </h3>
              <p>Year: {vehicle.year}</p>
              <p>Price(rs): {vehicle.price.toLocaleString()}</p>
              
              <button>View Details</button>
            </div>
          ))}
        </div>
      </div>
    </div></>
  );
};

export default VehicleCatalogPage;
